import { call, put, all, take, fork } from 'redux-saga/effects';

import * as appActions from '../app/app-actions';
import * as decksActions from '../decks/decks-actions';
import * as cardsActions from '../cards/cards-actions';

import { Actions as DeckActions } from '../decks/decks-types';
import { Actions as CardActions } from '../cards/cards-types';

function* fetchInitialData() {
  yield all([
    // throws fetching functions
    put(decksActions.fetchDecksRequest()),
    put(cardsActions.fetchCardsRequest()),
  ]);
}

export function* watchDecks() {
  yield take([DeckActions.FETCH_DECKS_SUCCESS, DeckActions.FETCH_DECKS_ERROR]);
  console.log('DECKS REQUEST END');
}

export function* watchCards() {
  yield all([
    // wait for all fetching to be finished and succedeed
    take(CardActions.FETCH_CARDS_SUCCESS),
    take(CardActions.FETCH_CARDS_ERROR)
  ]);
  console.log('hola')
}

// Call init app on saga load
export function* AppSaga() {
  yield call(fetchInitialData);

  const watchers = [
    fork(watchDecks),
  ];

  yield all(watchers);
  yield put(appActions.startInitApp());
}
