import { Reducer } from 'typesafe-actions';
import { ActionTypes, Actions, Card } from "./cards-types";
import { combineReducers } from 'redux';
import { updateBaseObjInArray, insertBaseObjInArray, removeBaseObjFromArray } from '../../store/utils';

export const cards:Reducer<Card[], ActionTypes> = (
  state = [],
  action: ActionTypes,
) => {
  switch(action.type){
		case Actions.ADD_CARD: 
			return insertBaseObjInArray(state, action.payload.card);
		case Actions.DELETE_CARD:
			return removeBaseObjFromArray(state, action.payload.uid);
    case Actions.FETCH_CARDS_SUCCESS:
			return action.payload.cards;
		case Actions.UPDATE_CARD:
			return updateBaseObjInArray(state, action.payload.card);
    default: return state;
  }
};

export const isFetching:Reducer<boolean, ActionTypes> = (
	state = false,
	action: ActionTypes,
) => {
	switch (action.type) {
    case Actions.FETCH_CARDS_REQUEST:
      return true;
    case Actions.FETCH_CARDS_SUCCESS:
			return false;
		case Actions.FETCH_CARDS_ERROR:
			return false;
		default:
			return state;
	}
};

export default combineReducers({
	isFetching: isFetching,
	cards: cards,
});
